mapper.select:
SELECT
*
FROM
assign_attach
where id = #{id}
and assign_code = #{assignCode}

mapper.update:
update assign_attach
set
content=#{content},uploadpath=#{uploadpath},filename=#{filename},filetype=#{filetype},updatedate=sysdate
where assign_code=#{assignCode}
and id=#{id}

delete
from assign_attach
where
assign_code=#{assignCode}
and id=#{id}

mapper.select:
select a.name, b.status, c.title, c.end_date,c.assign_code,a.id from member a , assign_attach b, assign c, class d where d.class_code=c.class_code and c.assign_code=b.assign_code and b.id=a.id and d.id=#{id}
and c.end_date>=sysdate
order by c.end_date

select a.title, a.end_date, d.name, d.id from assign a, class b, class_ing c, member d where a.class_code=b.class_code and b.class_code=c.class_code and c.id=d.id and b.id = #{id}
and a.end_date>=sysdate
order by a.end_date,a.title

select a.assign_code, a.id, a.status
from assign_attach a
where assign_code=#{assignCode}

mapper.update:
update assign	
   set uploadpath=null, filename=null, filetype=null
 where assign_code=#{assignCode}

update assign_attach 
   set confirm=#{confirm}
 where assign_code=#{assignCode} 
   and id=#{id}
