mapper.select:
select a.university_name
from university a,
pacounselt b
where a.univ_code=b.fu_code
and b.pc_code=#{pcCode}

select a.university_name
from university a,
pacounselt b
where a.univ_code=b.su_code
and b.pc_code=#{pcCode}

select a.university_name
from university a,
pacounselt b
where a.univ_code=b.tu_code
and b.pc_code=#{pcCode}

select avg(a.mygrade) as stuavg
from school_score a,
pacounselt b
where
a.id=b.client
and b.pc_code=#{pcCode}

select a.university_name from university a, school_score b, pacounselt c where b.id=client and (((a.university_kor+a.university_eng+a.university_math+a.university_social+a.university_science)/5)/b.mygrade)between 0.8 and 1.2 and
rownum <= 3
and c.pc_code=#{pcCode}

select a.university_name from university a, school_score b, pacounselt c where b.id=client and (((a.university_kor+a.university_eng+a.university_math+a.university_social+a.university_science)/5)/b.mygrade)<0.8
and rownum <= 3
and c.pc_code=#{pcCode}

select *
from(
select rownum as rnum,SearchNoticeResult.*
from (
select
a.university_name
from university a,
school_score b,
pacounselt c
where
b.id=client
and
(((a.university_kor+a.university_eng+a.university_math+a.university_social+a.university_science)/5)/b.mygrade)between
0.8 and 1.2

and c.pc_code=#{pcCode}
) SearchNoticeResult

    where rownum <= 2

)

where rnum > 1

select *
from(
select rownum as rnum,SearchNoticeResult.*
