mapper.select:

select *
from(
    select rownum as rnum,SearchSuggestResult.*
    from ( 
    	select * 
		from suggest
		where lcode is not null

		order by regDate desc
    )  SearchSuggestResult

    where rownum <= #{endRow}

)

where rnum > #{startRow}

select *
from(
    select rownum as rnum,SearchSuggestResult.*
    from ( 
    	select * 
		from suggest
		where lcode is not null

		order by likecnt desc
    )  SearchSuggestResult

    where rownum <= #{endRow}

)

where rnum > #{startRow}

mapper.update:
update suggest
set viewcnt=viewcnt+1
where lcode=#{lcode}

update suggest
set likecnt=likecnt+1
where lcode=#{lcode}
