mapper.select:
select b.class_code ,b.class_rank ,b.class_name ,b.id ,b.subdetail_code ,b.cpno ,b.start_date ,b.end_date ,b.class_price ,b.total_time ,b.class_grade ,b.subdetail_name ,b.subject_code ,b.subject_name ,b.name ,b.stu_cnt ,min(b.start_time) as mintime from (select a.class_code ,a.class_rank ,a.class_name ,a.id ,a.subdetail_code ,a.cpno ,a.start_date ,a.end_date ,a.class_price ,a.total_time ,a.class_grade ,a.stu_cnt ,b.subdetail_name ,b.subject_code ,c.subject_name ,d.name ,e.start_time from class a, subdetail b, subject c, member d, time_detail e, classroom f where a.subdetail_code = b.subdetail_code and b.subject_code=c.subject_code and a.id = d.id and e.class_code = a.class_code and e.room_code = f.room_code and a.cpcheck='3' and f.room_code = #{roomCode}
and a.start_date <= to_date(sysdate) and a.end_date>= to_date(sysdate) ) b where class_code is not null
group by b.class_code ,b.class_rank ,b.class_name ,b.id ,b.subdetail_code ,b.cpno ,b.start_date ,b.end_date ,b.class_price ,b.total_time ,b.class_grade ,b.subdetail_name ,b.subject_code ,b.subject_name ,b.name ,b.stu_cnt

select count(*) from ( select b.class_code ,b.class_rank ,b.class_name ,b.id ,b.subdetail_code ,b.cpno ,b.start_date ,b.end_date ,b.class_price ,b.total_time ,b.class_grade ,b.subdetail_name ,b.subject_code ,b.subject_name ,b.name ,min(b.start_time) as mintime from (select a.class_code ,a.class_rank ,a.class_name ,a.id ,a.subdetail_code ,a.cpno ,a.start_date ,a.end_date ,a.class_price ,a.total_time ,a.class_grade ,b.subdetail_name ,b.subject_code ,c.subject_name ,d.name ,e.start_time from class a, subdetail b, subject c, member d, time_detail e, classroom f where a.subdetail_code = b.subdetail_code and b.subject_code=c.subject_code and a.id = d.id and e.class_code = a.class_code and e.room_code = f.room_code and a.cpcheck='3' and f.room_code = #{roomCode}
and a.start_date <= to_date(sysdate) and a.end_date>= to_date(sysdate) ) b where class_code is not null
group by b.class_code ,b.class_rank ,b.class_name ,b.id ,b.subdetail_code ,b.cpno ,b.start_date ,b.end_date ,b.class_price ,b.total_time ,b.class_grade ,b.subdetail_name ,b.subject_code ,b.subject_name ,b.name)

select a.class_code ,a.start_date ,a.end_date ,a.total_time ,b.time_code ,b.room_code ,b.start_time ,b.end_time ,b.classday ,b.ctperiod from class a, time_detail b where a.class_code = b.class_code
and to_date(start_date)<=to_date(sysdate) and to_date(end_date)>=to_date(sysdate)
and a.cpcheck='3' and b.room_code = #{roomCode} and a.class_code = #{classCode} and b.classday=#{classday}

mapper.update:
update classroom
set
	capacity=#{capacity}
	,room_no=#{roomNo}
	,room_floor=#{roomFloor}
	,picture=#{picture}
	,use_purpose=#{usePurpose}
where
	room_code = #{roomCode}

mapper.select:
select a.class_code ,a.id ,a.start_date ,a.end_date ,a.total_time ,b.time_code ,b.room_code ,b.start_time ,b.end_time ,b.classday ,b.ctperiod from class a, time_detail b where a.class_code = b.class_code
and to_date(start_date)<=to_date(sysdate) and to_date(end_date)>=to_date(sysdate)
and a.cpcheck='3' and room_code = #{roomCode}
