mapper.update:
	insert into member(
    picture
    ,id
    ,name
    ,authority
    ,school_name
    ,grade
    ,phone
    ,parent_name
    ,parent_phone
    ,regDate
    ,school_code
    ,address
    ,detail_address)
values(
     #{picture}
    , #{id,jdbcType=VARCHAR}
    ,#{name,jdbcType=VARCHAR}
    ,#{authority,jdbcType=VARCHAR}
     ,(
    select school_name
        from school_detail
       where school_name like '%'||#{schoolName}||'%'
    )
    ,#{grade,jdbcType=VARCHAR}
    ,#{phone,jdbcType=VARCHAR}
    ,#{parentName, jdbcType=VARCHAR}
    ,#{parentPhone, jdbcType=VARCHAR}
    ,to_date(sysdate)
    ,(select school_code
        from school_detail
       where school_name like '%'||#{schoolName}||'%'
    )
    ,#{address}
    ,#{detailAddress}

)

mapper.select:
select a.university_name from university a,
sh_univ c where a.univ_code=c.univ_code1 or a.univ_code=c.univ_code2 or a.univ_code=c.univ_code3
and c.id=#{id}
