mapper.update:
update class
set cpno=#{cpno},cpcheck=#{cpcheck}
where class_code=#{classCode}

update weekplan
set 
	week=#{week},
	content=#{content}
where wpno=#{wpno}

update homeworkplan
set 
	no=#{no},
	content=#{content}
where homeworkplan=#{homeworkplan}

update class_plan
set
	cphead=#{cphead},
	textbook=#{textbook}
where cpno=#{cpno}

mapper.select:
		select count(*) 
     	  from weekplan
     	 where cpno=#{cpno}

		select review_code,star_score,title, content
          from class_review
         where class_code=#{classCode}

select *
  from class_review
 where review_code=#{reviewCode}
